import styled, { css } from 'styled-components';
import { device } from '@styles/tools/breakpoints.styles';

export const ButtonContactStyled = styled.button`
  display: flex;
  background: none;
  align-items: center;
  justify-content: center;
  color: inherit;
  border: none;
  padding: 0;
  font: inherit;
  cursor: pointer;
  outline: inherit;
  background-color: ${(props) => props.theme.colors.black8};
  border-radius: 2px;
  padding: 0 30px;
  color: ${(props) => props.theme.colors.black0};
  font-size: 1.4rem;
  font-weight: 500;

  ${(props) =>
    props.isMobile &&
    css`
      display: none;
      ${device.mobile} {
        display: block;
      }

      ${device.tablet} {
        display: block;
      }
    `}

  ${(props) =>
    props.isWeb &&
    css`
      display: block;
      ${device.mobile} {
        display: none;
      }

      ${device.tablet} {
        display: none;
      }
    `}
`;
