import React, { useState } from 'react';
import Modal from 'react-modal';
import { ButtonContactStyled } from './ButtonContact.styles';

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
};

const ButtonContact = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const handleModal = () => setModalIsOpen(!modalIsOpen);

  return (
    <>
      <ButtonContactStyled isWeb onClick={handleModal}>
        Entre em contato
      </ButtonContactStyled>
      <ButtonContactStyled isMobile>Entre em contato</ButtonContactStyled>

      <Modal isOpen={modalIsOpen} onRequestClose={handleModal} style={customStyles} contentLabel="Example Modal">
        <h2>Entre em contato em</h2>
        <p>Você será respondido o mais breve possível</p>
        <button type="button" onClick={handleModal}>
          Fechar
        </button>
      </Modal>
    </>
  );
};

export default ButtonContact;
