import styled, { css } from 'styled-components';
import SvgIcon from '@components/svgIcon/SvgIcon';

export const ButtonContactWebStyled = styled.button`
  display: flex;
  background: none;
  align-items: center;
  justify-content: center;
  color: inherit;
  border: none;
  padding: 0;
  font: inherit;
  cursor: pointer;
  outline: inherit;
  background-color: ${(props) => props.theme.colors.black8};
  border-radius: 2px;
  padding: 0 ${(props) => props.theme.global.gutterSpace * 1.5}px;
  color: ${(props) => props.theme.colors.black0};
  font-size: 1.4rem;
  font-weight: 500;
  display: block;

  ${(props) =>
    props.onlyIcon &&
    css`
      background-color: transparent;
      padding: ${props.theme.global.gutterSpace / 2}px;
    `}
`;

export const ButtonClose = styled.button`
  display: flex;
  background: none;
  align-items: center;
  justify-content: center;
  color: inherit;
  border: none;
  padding: 0;
  font: inherit;
  cursor: pointer;
  outline: inherit;
  background-color: transparent;
  border-radius: 2px;
  padding: 10px;
  color: ${(props) => props.theme.colors.black0};
  font-size: 1.4rem;
  font-weight: 500;
`;

export const LinkContactWebStyled = styled.a`
  display: flex;
  position: relative;
  background: none;
  align-items: center;
  justify-content: center;
  color: inherit;
  border: none;
  padding: 0;
  font: inherit;
  cursor: pointer;
  outline: inherit;
  background-color: transparent;
  border-radius: 2px;
  padding: 0;
  color: ${(props) => props.theme.colors.black0};
  font-size: 1.6rem;
  font-weight: 600;
  padding: ${(props) => props.theme.global.gutterSpace / 4}px;

  margin-bottom: ${(props) => props.theme.global.gutterSpace / 2}px;

  @keyframes shake {
    0% {
      transform: translate(1px, 1px) rotate(0deg);
    }
    25% {
      transform: translate(-1px, -2px) rotate(-1deg);
    }
    50% {
      transform: translate(-3px, 0px) rotate(1deg);
    }
    75% {
      transform: translate(3px, 2px) rotate(0deg);
    }
    100% {
      transform: translate(1px, -1px) rotate(1deg);
    }
  }

  &:after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    border-bottom: 2px solid ${(props) => props.theme.colors.black5};
    width: 0%;
    transition: width 150ms linear;
  }

  &:hover {
    &:after {
      content: '';
      width: 100%;
    }

    ${CustomIcon} {
      animation: shake 500ms;
    }
  }
`;

export const CustomIcon = styled(SvgIcon)``;
