import React, { useState } from 'react';
import Modal from 'react-modal';
import { ButtonContactStyled } from './ButtonContact.styles';
import { Box } from '@components/slyce/slyce';
import { theme } from '@styles/theme/theme';

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
    transition: 'transform 300ms ease-in-out',
  },
};

const ButtonContact = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const handleModal = () => setModalIsOpen(!modalIsOpen);

  return (
    <>
      <ButtonContactStyled isWeb onClick={handleModal}>
        Entre em contato
      </ButtonContactStyled>
      <ButtonContactStyled isMobile>Entre em contato</ButtonContactStyled>

      <Modal isOpen={modalIsOpen} onRequestClose={handleModal} style={customStyles} contentLabel="Contato">
        <Box padding="10px 20px">
          <Box>
            <Box as="h2" color={theme.colors.black8}>
              Contato
            </Box>
          </Box>

          <Box padding="10px 0">
            <Box as="p">Whatsapp: (99) 9999-9999 </Box>

            <Box as="p">E-email: contato@contato.com </Box>
          </Box>
          {/* <button type="button" onClick={handleModal}>
            Fechar
          </button> */}
        </Box>
      </Modal>
    </>
  );
};

export default ButtonContact;
