import styled from 'styled-components';
import { device } from '@styles/tools/breakpoints.styles';

export const IntroduceSectionContainer = styled.section`
  position: relative;
  min-height: 1112px;
  width: 100%;
  z-index: 1;
  background-color: #cfcfcf0f;
  ${device.desktop} {
    padding: 200px 160px 200px 160px;
  }

  ${device.tablet} {
    padding: 200px 60px 200px 60px;
    display: flex;
    justify-content: center;
    min-height: 0;
  }

  ${device.mobile} {
    display: flex;
    justify-content: center;
    padding: 120px 30px 120px 30px;
    min-height: 0;
  }
`;

export const Introduce = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const TitleIntroduce = styled.h1`
  font-size: 3rem;
  color: ${(props) => props.theme.colors.black8};
  flex-wrap: wrap;
  text-align: center;
  padding-bottom: ${(props) => props.theme.global.gutterSpace}px;
`;

export const DescriptionIntroduce = styled.h2`
  font-size: 1.6rem;
  font-weight: 400;
  color: ${(props) => props.theme.colors.black7};
  flex-wrap: wrap;
  text-align: justify;
`;

export const ProfileImg = styled.img`
  border-radius: 30px;
`;
