import styled, { css } from 'styled-components';
import { device } from '@styles/tools/breakpoints.styles';

export const ButtonContactWebStyled = styled.button`
  display: flex;
  background: none;
  align-items: center;
  justify-content: center;
  color: inherit;
  border: none;
  padding: 0;
  font: inherit;
  cursor: pointer;
  outline: inherit;
  background-color: ${(props) => props.theme.colors.black8};
  border-radius: 2px;
  padding: 0 ${(props) => props.theme.global.gutterSpace * 1.5}px;
  color: ${(props) => props.theme.colors.black0};
  font-size: 1.4rem;
  font-weight: 500;
  display: block;

  ${device.mobile} {
    display: none;
  }

  ${device.tablet} {
    display: none;
  }

  ${(props) =>
    props.onlyIcon &&
    css`
      background-color: transparent;
      padding: 0 20px;
    `}
`;

export const ButtonClose = styled.button`
  display: flex;
  background: none;
  align-items: center;
  justify-content: center;
  color: inherit;
  border: none;
  padding: 0;
  font: inherit;
  cursor: pointer;
  outline: inherit;
  background-color: transparent;
  border-radius: 2px;
  padding: 10px;
  color: ${(props) => props.theme.colors.black0};
  font-size: 1.4rem;
  font-weight: 500;
`;

export const LinkContactWebStyled = styled.a`
  display: flex;
  background: none;
  align-items: center;
  justify-content: center;
  color: inherit;
  border: none;
  padding: 0;
  font: inherit;
  cursor: pointer;
  outline: inherit;
  background-color: ${(props) => props.theme.colors.black8};
  border-radius: 2px;
  padding: 0 ${(props) => props.theme.global.gutterSpace * 1.5}px;
  color: ${(props) => props.theme.colors.black0};
  font-size: 1.4rem;
  font-weight: 500;
  display: none;

  &:link {
    text-decoration: none;
  }

  &:visited {
    text-decoration: none;
  }

  &:hover {
    text-decoration: none;
  }

  &:active {
    text-decoration: none;
  }

  ${device.mobile} {
    display: flex;
  }

  ${device.tablet} {
    display: flex;
  }

  ${(props) =>
    props.onlyIcon &&
    css`
      background-color: transparent;
    `}
`;
