import React, { useState } from 'react';
import Modal from 'react-modal';
import { ButtonContactStyled } from './ButtonContact.styles';
import { Box } from '@components/slyce/slyce';
import { theme } from '@styles/theme/theme';
import { LinkSocialnetwork } from '@components/ContactSection/ContactSection.styles';
import SvgIcon from '@components/svgIcon/SvgIcon';

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
    transition: 'transform 300ms ease-in-out',
  },
};

const ButtonContact = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const handleModal = () => setModalIsOpen(!modalIsOpen);

  return (
    <>
      <ButtonContactStyled isWeb onClick={handleModal}>
        Entre em contato
      </ButtonContactStyled>
      <ButtonContactStyled isMobile>Entre em contato</ButtonContactStyled>

      <Modal isOpen={modalIsOpen} onRequestClose={handleModal} style={customStyles} contentLabel="Contato">
        <Box padding="10px 20px" maxWidth="400px">
          <Box>
            <Box as="h2" color={theme.colors.black8}>
              Contato
            </Box>
          </Box>

          <Box padding="10px 0" display="flex" alignItems="center" flexDirection="column">
            <img src="/static/assets/images/qrcode.png" width="100%" alt="QRCODE do Whatsapp" />
            <Box display="flex" alignItems="center" justifyContent="center">
              <SvgIcon src="/static/assets/icons/whatsapp.svg" height="20px" width="20px" fill={theme.colors.black8} />
              <Box as="p" ml="10px" mb="20px">
                (99) 9999-9999
              </Box>
            </Box>
            <Box as="p">
              Se preferir pode nos enviar um email para <strong>contato@contato.com</strong> que iremos entrar em conato o quanto antes
            </Box>
          </Box>
          {/* <button type="button" onClick={handleModal}>
            Fechar
          </button> */}
        </Box>
      </Modal>
    </>
  );
};

export default ButtonContact;
